import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TodoList.css";

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [dueDate, setDueDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [taskState, setTaskState] = useState("todo");

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/todos")
      .then((response) => {
        setTodos(response.data);
      })
      .catch((error) => {
        console.error("Error fetching todos:", error);
      });
  }, []);

  const handleStateChange = (id, currentState) => {
    let newState;
    if (currentState === "todo") {
      newState = "inprogress";
    } else if (currentState === "inprogress") {
      newState = "done";
    } else {
      // If the current state is "done", you may choose to handle it differently
      // For example, you could set it back to "todo" or leave it as is
      newState = currentState;
    }

    axios
      .put(`http://localhost:8080/api/todos/${id}`, { state: newState })
      .then((response) => {
        const updatedTodos = todos.map((todo) => {
          if (todo._id === id) {
            todo.state = newState;
          }
          return todo;
        });
        setTodos(updatedTodos);
      })
      .catch((error) => {
        console.error("Error changing state:", error);
      });
  };

  const handleAddTodo = () => {
    axios
      .post("http://localhost:8080/api/todos", {
        title: newTodo,
        description: newDescription,
        dueDate: dueDate,
        state: taskState,
        completed: false,
      })
      .then((response) => {
        setTodos([...todos, response.data]);
        setNewTodo("");
        setNewDescription("");
        setDueDate(new Date().toISOString().split("T")[0]);
      })
      .catch((error) => {
        console.error("Error adding todo:", error);
      });
  };

  const handleUpdateTodo = (id, completed) => {
    axios
      .put(`http://localhost:8080/api/todos/${id}`, { completed: !completed })
      .then((response) => {
        const updatedTodos = todos.map((todo) => {
          if (todo._id === id) {
            todo.completed = !completed;
          }
          return todo;
        });
        setTodos(updatedTodos);
      })
      .catch((error) => {
        console.error("Error updating todo:", error);
      });
  };

  const handleDeleteTodo = (id) => {
    axios
      .delete(`http://localhost:8080/api/todos/${id}`)
      .then(() => {
        const updatedTodos = todos.filter((todo) => todo._id !== id);
        setTodos(updatedTodos);
      })
      .catch((error) => {
        console.error("Error deleting todo:", error);
      });
  };

  return (
    <div className="container">
      <h1 className="title">Todo List</h1>
      <div className="input-container">
        <input
          className="input"
          type="text"
          placeholder="Task title"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
        />
        <input
          className="input"
          type="text"
          placeholder="Description"
          value={newDescription}
          onChange={(e) => setNewDescription(e.target.value)}
        />
        <input
          className="input"
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <select
          className="select"
          value={taskState}
          onChange={(e) => setTaskState(e.target.value)}
        >
          <option value="todo">Todo</option>
          <option value="inprogress">In Progress</option>
          <option value="done">Done</option>
        </select>
        <button className="add-button" onClick={handleAddTodo}>
          Add
        </button>
      </div>
      <ul className="todo-list">
        {todos.map((todo) => (
          <li key={todo._id} className="todo-item">
            <input
              className="checkbox"
              type="checkbox"
              checked={todo.completed}
              onChange={() => handleUpdateTodo(todo._id, todo.completed)}
            />
            <strong className="todo-title">{todo.title}</strong>
            <p className="todo-description">{todo.description}</p>
            <p className="todo-due-date">Due Date: {todo.dueDate}</p>
            <p className="todo-state">State: {todo.state}</p>
            <button
              className="delete-button"
              onClick={() => handleDeleteTodo(todo._id)}
            >
              Delete
            </button>
            <button
              className="change-state-button"
              onClick={() => handleStateChange(todo._id, "inprogress")} // Change state to "inprogress"
            >
              Change State
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
